        'samlIDPMetaDataXML' => {
            'test' => sub {
                my $v = shift();
                return 1 unless $v and %$v;
                my @msg;
                my $res = 1;
                my %entityIds;
                foreach my $idpId ( keys %$v ) {
                    unless ( $v->{$idpId}{'samlIDPMetaDataXML'} =~
                        /entityID="(.+?)"/is )
                    {
                        push @msg, "$idpId SAML metadata has no EntityID";
                        $res = 0;
                        next;
                    }
                    my $eid = $1;
                    if ( defined $entityIds{$eid} ) {
                        push @msg,
"$idpId and $entityIds{$eid} have the same SAML EntityID";
                        $res = 0;
                        next;
                    }
                    $entityIds{$eid} = $idpId;
                }
                return $res, join( ', ', @msg );
            },
            'type' => 'file'
        },
